INSERT INTO `oferta_academica` (`id`, `caracter`, `carga_horaria`, `contenidos_minimos`, `actividad_id`, `area_id`, `campo_id`, `carrera_id`, `unidad_id`) VALUES
(1, 'Obligatoria', '80', 'Modelos químicos de sistemas en equilibrio: concepto cinético y termodinámico. Equilibrio ácido-base. El agua y el comportamiento ácido-base de sus soluciones. Soluciones reguladoras de pH. Equilibrio redox. Electroquímica. Procesos redox en medios naturales. Compuestos de coordinación en sistemas naturales. Quelatos. Acuocomplejos. Equilibrio de iones complejos. Solubilidad. Equilibrios de precipitación. Procesos de precipitación en sistemas naturales. Las interacciones químicas en la naturaleza. Equilibrios múltiples. Operaciones básicas del método analítico. Análisis cuantitativos de elementos y compuestos de interés agronómico. Gravimetría, volumetría. Métodos instrumentales: conductimetría, potenciometría, espectrocopía molecular y atómica.', 1, 1, 1, 1, 1),
(2, 'Obligatoria', '64', 'Reconocimiento de los grupos funcionales en las estructuras de las biomoléculas. Reacciones de óxidos-reducción en relación con los procesos anabólicos y catabólicos. Estado físico e interacciones entre moléculas. Comportamiento ácido-báse. Metabólicos primarios y secundarios. Isometría. Estereoquímica. Su rol biológico.Lípidos simples y compuestos. Propiedades fisicas, quimicas y biológicas. Clasificación, estructura y funciones. Sustancias ópticamente activas. Hidratos de carbono. Propiedades químicas, físicas y biológicas. Clasificación, estructura y funciones. Aminoácidos y proteínas. Propiedades, físicas, químicas y biológicas.', 2, 2, 1, 1, 1),
(3, 'Obligatoria', '48', 'Medición directa e indirecta de magnitudes. Indeterminaciones de apreciación y estadística. Propagación de indeterminaciones en situaciones de interés agroambiental. Condiciones de equilibrio de cuerpos rígidos. Fuerzas de vínculo. Movimiento circular. Aplicaciones a maquinarias. Estática y dinámica de fluídos: aplicación agronómica y ambiental. Fluídos viscosos. Tensión superficial. Potencial agua en el suelo. Medios porosos: Ley de Darcy. Conductividad hidráulica saturada y su determinación experimental en laboratorio. Aplicaciones al suelo y al agua subterránea. Difusión de gases en suelos. Transmición total. Aplicaciones a invernadero. Fotón. Absorción de la energía de la radiación electromagnética por pigmentos vegetales. Fuentes luminosas puntuales. Lámparas. Espectros de emisión. Calidad, intensidad y duración de la iluminación sobre especies vegetales.', 3, 3, 1, 1, 1),
(4, 'Obligatoria', '80', 'Distribución de frecuencias, medidad de posición y dispersión. Teoría de probabilidades: experimento aleatorio, concepto y axiomas de probabilidad. Variable aleatoria, modelos de distribución de probabilidades, parámetros. Población y muestra. Propiedades estadísticas de la media muestral. Estimación de parámetros. Intervalos de confianza. Pruebas de hipótesis. Comparación de promedios. Análisis de regresión lineal simple. Análisis de datos categóricos.', 4, 4, 2, 1, 1),
(5, 'Obligatoria', '64', 'Diversidad y clasificación de los grandes grupos de plantas. Nomenclatura botánica. Ciclo de vida de las plantas con semilla. Estructura del cormo típico. Ramificación e inflorescencia. Células y los tejidos vegetales, y sus funciones básicas. Crecimientos primario y secundario. Exomorfología y anatomía de los órganos vegetales. Diversidad morfológica del cormo y del hábito de crecimiento. Hábitat. Modalidad nutricional de las plantas. Reproducción: esperogénesis y gametogénesis, mecanismos de polinización, sistemas reproductivos, fecundación. Ontogenia de la semilla y del fruto. Apormixis y partenoscarpia. Dispersión: agentes dispersores y tipos de diásporas. Germinación. Morfología de las plántulas.', 5, 5, 1, 1, 1),
(6, 'Obligatoria', '80', 'Génesis de suelo: Rocas. Minerales y Meteorización. Física de suelos: Textura. Escritura. Densidad y otras propiedades del suelo Agua del suelo. Físico-química de suelos: coloides del suelo. Capacidad de intercambio catiónico (CIC) y catiónicos y aniones de cambio. Química de suelos: Reacción del suelo. Ciclos naturales de los elementos. Materia orgánica. Humificación. Morfología y Clasificación: Reconocimiento morfológico de suelos. Taxonomía de suelos.', 6, 6, 1, 1, 1),
(7, 'Obligatoria', '64', 'Metolodología y climatologia: sistema climático, factores externos e internos. Tiempo y clima. Elementos y factores. La atmósfera, composición y estratificación. La Tierra, movimientos y consecuencias. Energía atmosférica: emisión solar. Efecto de la atmósfera sobre la radiación. Radiación sobre la superficie terrestre. Emisión terrestre y atmosférica. Balance de radiación. Proceso de calentamiento y enfriamiento de la atmósfera. Temperatura del suelo y del aire. Variación diaria, anal y asincrónica de la temperatura. Ciclo hidrológico: humedad atmosférica. Condensación y sublimación. Precipitación: causas y formas. Tipos genéticos. Regímenes. Evaporación y evapotranspiración potencial y real. Balance de agua del suelo. Movimiento de la atmósfera: circulación general de la atmósfera. Circulaciones locales. Masas de aire. Frentes. Variabilidad y cambio climático: definiciones. Causas naturales y antrópicas. Fundamentos de Bio y Agroclimatología: concepto. Fenología: observación en vegetales espontáneos y cultivados y en animales silvestres y domésticos. Métodos de investigación bioclimática. Elementos climáticos determinantes del crecimiento y/o desarrollo de los cultivos: radiación, temperatura, agua edáfica. El tiempo y el clima y las enfermedades y plagas de los cultivos y animales domésticos. Adversidades climáticas: heladas, sequías, granizo, viento. Impacto de la variabilidadad y cambio climático sobre los procesos productivos agropecuarios. Indicadores de deterioro ambiental producido por la actividad agropecuaria. Clima argentino.', 7, 6, 1, 1, 1),
(8, 'Obligatoria', '64', 'Caracteres morfológicos y reproductivos de las diferentes divisiones del Reino Vegetal. Clasificación de las Espermatófitas Subdivisiones, Clases, Órdenes y Familias. Caracteres morfológicos de valor taxonómico en los diferentes grupos. Reglas básicas de nomenclatura botánica. Técnica de herborarización. Uso de claves. Interpretación de descripciones y determinación de ejemplares silvestres y cultivados. Especies de importancia económica: cereales y pseudocereales, forestales, forrajeras, hortícolas, frutales, oleaginosas e industriales. Principales especies nativas. Malezas.', 8, 5, 1, 1, 1),
